query GetMultipleAccounts($validAccountId: ID!, $invalidAccountId: ID!) {
    validAccount: accountById(accountId: $validAccountId) {
        ...BankAccountDetails
    }
    invalidAccount: accountById(accountId: $invalidAccountId) {
        ...BankAccountDetails
    }
}

fragment BankAccountDetails on BankAccountType {
    id
    currency
    client{
      id
      firstName
      middleName
      lastName
    }
    transferLimit
    accountCreateDate
}
